mapper.select:
SELECT COUNT(*)
FROM BIZ
WHERE BSMNO = #{bsmno}
AND PASS = #{pass}

mapper.update:
UPDATE BIZ
SET
PASS = #{bpchange}
WHERE BSMNO = #{bsmno}

UPDATE IMAGE
SET					
ORIGINALFILENAME = #{originalfilename},
STOREDFILENAME = #{storedfilename},
FILESIZE = #{filesize}
WHERE ATTACHID = #{attachid}
